


сан1 = int(input(''))
операция = input('')
сан2 = int(input(" "))


if операция == "+":
    жыйынтык = сан1 + сан2
    print( жыйынтык)
elif операция == "-":
    жыйынтык = сан1 - сан2
    print(жыйынтык)
elif операция == "*":
    жыйынтык = сан1 * сан2
    print( жыйынтык)
elif операция == "/":
    if сан2 != 0:
        жыйынтык = сан1 / сан2
        print( жыйынтык)
    else:
        print("0го бөлүүгө болбойт!")
else:
    print("Мындай операция жок!")